import ReactDOM from 'react-dom';
import './index.css';
const root = ReactDOM.createRoot(document.getElementById('root'));

const App = (props) => {
  return <h1>{props.counter}</h1>
}

let counter = 0
const refresh = () =>{
  ReactDOM.render(
    <React.StrictMode>
      <App counter={counter} />
    </React.StrictMode>
  );
}

setInterval(() =>{
  counter++
  refresh()
},1000)
