import React from 'react';
import { Link } from 'react-router-dom';
import Navbar from '../components/NavigationBar/Navbar';
import Footer from '../components/Footer/Footer';
import ImageComponent from '../components/ImageComponent/ImageComponent';
import ColumnLayout from '../components/ColumnLayout/ColumnLayout';
import { CenteredColoredBox, ColoredBox } from '../components/ColoredBox/ColoredBox';
import EquipHuma from '../images/backgroundImage.jpg';
import Logo from '../images/logoPastorets.png';

const Equip = () => {
  const boxes = [
    { width: '480px', height: '400px', backgroundColor: '#C41919', text: "Equip tècnic", link: "/tecnics" },
    { width: '480px', height: '400px', backgroundColor: '#54FF45', text: "Actors i Actrius", link: "/a&a" },
    { width: '480px', height: '400px', backgroundColor: '#D9D9D9', text: "Ajudants de producció", link: "/ajudants-produccio" },
  ];

  return (
    <div>
      <Navbar />
      <ImageComponent src={EquipHuma} alt="Equip huma" text="Equip humà" />
      <CenteredColoredBox
        width="1440px"
        height="530px"
        backgroundColor="#F1F1F1"
        src={Logo}
        alt="Logo"
        text="L'Associació Pastorets de Girona, està formada per persones professionals dels diferents àmibts teatrals i persones amateurs amb una àmplia experiència en el món dels Pastorets de Girona"
      />
      <ColumnLayout
        boxes={boxes.map((box, index) => (
          <div key={index} className="column">
            <div className="column-content">
              <Link to={box.link}>
                <ColoredBox {...box} />
              </Link>
            </div>
          </div>
        ))}
        centered={true}
      />
      <ColoredBox width="1440px" height="350px" backgroundColor="#fff" />
      <Footer />
    </div>
  );
};

export default Equip;
