import styled from 'styled-components';
import { Link } from 'react-router-dom';

export const FooterContainer = styled.footer`
  background-color: #607D3B; /* Color de fons del footer */
`;



export const SocialIcons = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 240px;
`;

export const SocialIconLinkWrapper = styled.div`
  display: flex;
  align-items: center;
`;

export const SocialIconLink = styled.a`
  color: #000;
  font-size: 24px;
  margin-right: 8px; /* Add desired spacing here */
  text-decoration: none; /* Remove underline */
  display: flex;
  align-items: center;

  &:hover {
    transition: 0.3s ease-out;
  }
`;


export const InstagramIconLink = styled(SocialIconLink)`
  &:hover {
    background-image: linear-gradient(to right, #833ab4, #fd1d1d, #fcb045);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    color: #fcb045; /* Instagram color */
  }
`;


export const FacebookIconLink = styled(SocialIconLink)`
  &:hover {
    color: #3b5998; /* Blue hover color for Facebook */
  }
`;

export const YoutubeIconLink = styled(SocialIconLink)`
  &:hover {
    color: #c4302b; /* Red hover color for YouTube */
  }
`;

export const TwitterIconLink = styled(SocialIconLink)`
  &:hover {
    color: #1da1f2; /* Blue hover color for Twitter */
  }
`;
