import React from 'react';
import TeamMemberCard from '../components/TeamMemberCard/TeamMemberCard';
import castingIcon from '../images/castingIcon.png';
import Navbar from '../components/NavigationBar/Navbar'
import Footer from '../components/Footer/Footer'
import ImageComponent from '../components/ImageComponent/ImageComponent'
import EquipTecnic from '../images/backgroundImage.jpg'
const App = () => {
  const teamMembers = [
    {
      name: "Salvador de Castro",
      role: "Director",
      bio: "Text breu",
      imageUrl: castingIcon,
      twitterLink: "https://twitter.com",
      instagramLink: "https://www.instagram.com"
    },
    {
      name: "Núria Donato",
      role: "Ajudant de direcció",
      bio: "Text breu",
      imageUrl: castingIcon
    },
    {
      name: "Alba Saló",
      role: "Gestió de Comunicació i Màrquesting",
      bio: "Text breu",
      imageUrl: castingIcon
    },
    {
      name: "Paula Saló",
      role: "Gestió de Comunicació i Màrqueting",
      bio: "Text breu",
      imageUrl: castingIcon
    },
    {
      name: "Natàlia Donato",
      role: "Tècnic de caracterització & Disseny de vestuari",
      bio: "Text breu",
      imageUrl: castingIcon
    },
    {
      name: "Josep Mitjà",
      role: "Gestió administrativa",
      bio: "Text breu",
      imageUrl: castingIcon
    },
    {
      name: "Marta Nadal Jimenez",
      role: "Tècnic de llums i so",
      bio: "Text breu",
      imageUrl: castingIcon
    },
    {
      name: "Daniel Moreta",
      role: "Direcció musical",
      bio: "Text breu",
      imageUrl: castingIcon
    },
    {
      name: "Isaac d'Aiguaviva",
      role: "Disseny d'escenografía",
      bio: "Text breu",
      imageUrl: castingIcon
    },
    {
      name: "Pau Morales",
      role: "Dissenyador gràfic",
      bio: "Text breu",
      imageUrl: castingIcon
    },
    {
      name: "Joan Cano",
      role: "Gestió de personal",
      bio: "Text breu",
      imageUrl: castingIcon
    },
    {
      name: "Carme Parramon",
      role: "Dissenyador gràfic",
      bio: "Text breu",
      imageUrl: castingIcon
    },
  ];

  return (
    <div>
      <Navbar />
      <ImageComponent src={EquipTecnic} alt="Equip tecnic" text="Equip Tècnic" />
      <div className="team-grid">
      <h1>Our Team</h1>
      <div className="team-grid__cards">
        {teamMembers.map((teamMember, index) => (
          <TeamMemberCard
            key={index}
            name={teamMember.name}
            role={teamMember.role}
            bio={teamMember.bio}
            imageUrl={teamMember.imageUrl}
            twitterLink={teamMember.twitterLink}
            instagramLink={teamMember.instagramLink}
          />
        ))}
      </div>
    </div>
      <Footer />
    </div>

  );
};

export default App;
