import ReactDOM from 'react-dom';
const root = ReactDOM.createRoot(document.getElementById('root'));

const App = (props) => {
  return <h1>{props.counter}</h1>
}

let counter = 0
const refresh = () =>{
  ReactDOM.render(
      <App counter={counter} />
  );
}

setInterval(() =>{
  counter++
  refresh()
},1000)
