import React from 'react';
import './ImageComponent.css'; // Import the CSS file for styling

const ImageComponent = ({ src, alt, text }) => {
  const divStyle = {
    backgroundImage: `url(${src})`,
  };

  return (
    <div className="image-component" style={divStyle}>
      <div className="text-overlay">
        <p>{text}</p>
      </div>
    </div>
  );
};

export default ImageComponent;
