import React from 'react';


const ColoredBox = ({ width, height, backgroundColor, text }) => {
  const boxStyle = {
    width: width,
    height: height,
    backgroundColor: backgroundColor,
  };

  return (
    <div className="colored-box" style={boxStyle}>
      {text && <p>{text}</p>}
    </div>
  );
};

export default ColoredBox;
