import React from 'react';
import './ColoredBox.css';

const ColoredBox = ({ width, height, backgroundColor, text, text2, src, alt, hasCircle }) => {
  const boxStyle = {
    width: width,
    height: height,
    backgroundColor: backgroundColor,
  };

  return (
    <div className="colored-box" style={boxStyle}>
      {hasCircle && <div className="red-circle"></div>}
      {src && <img src={src} alt={alt} className="box-image" />}
      {text && (
        <div>
          <p>{text}</p>
          <p>{text2}</p>
        </div>
      )}
    </div>
  );
};

export default ColoredBox;


const CenteredColoredBox = ({ width, height, backgroundColor, text, text2, src, alt, hasCircle }) => {
  const boxStyle = {
    width: width,
    height: height,
    backgroundColor: backgroundColor,
  };

  return (
    <div className="centered-colored-box" style={boxStyle}>
      {hasCircle && <div className="red-circle"></div>}
      {src && <img src={src} alt={alt} className="box-image" />}
      {text && (
        <div>
          <p>{text}</p>
          <p>{text2}</p>
        </div>
      )}
    </div>
  );
};

export { ColoredBox, CenteredColoredBox };
