import React from 'react';
import { Link } from 'react-router-dom';
import './ColoredBox.css';

const ColoredBox = ({ width, height, backgroundColor, text, text2, src, alt, link }) => {
  const boxStyle = {
    width: width,
    height: height,
    backgroundColor: backgroundColor,
    cursor: link ? 'pointer' : 'default', // Set cursor style to pointer if link is provided
  };

  const handleClick = () => {
    if (link) {
      // Navigate to the specified link
      window.location.href = link;
    }
  };

  return (
    <div className="colored-box" style={boxStyle} onClick={handleClick}>
      {src && <img src={src} alt={alt} className="box-image" />}
      {text && (
        <div>
          <p>{text}</p>
          <p>{text2}</p>
        </div>
      )}
    </div>
  );
};

const CenteredColoredBox = ({ width, height, backgroundColor, text, text2, src, alt, link }) => {
  const boxStyle = {
    width: width,
    height: height,
    backgroundColor: backgroundColor,
    cursor: link ? 'pointer' : 'default', // Set cursor style to pointer if link is provided
  };

  const handleClick = () => {
    if (link) {
      // Navigate to the specified link
      window.location.href = link;
    }
  };

  return (
    <div className="centered-colored-box" style={boxStyle} onClick={handleClick}>
      {src && <img src={src} alt={alt} className="box-image" />}
      {text && (
        <div>
          <p>{text}</p>
          <p>{text2}</p>
        </div>
      )}
    </div>
  );
};

export { ColoredBox, CenteredColoredBox };
